import React from 'react'

const Index = (props) => {
    return(
        <div className='container mx-auto px-4' >
            <h1 className='text-3xl' >Olá, eu sou o Marcos Gray</h1>

            <h2 className='font-bold text-2xl' >Meus repositórios no GitHub</h2>
            <pre> 
                {props.repos.map(
                    repo => {
                        return(
                            <div key={repo.id} className='rounded-lg bg-gray-200 mx-8 my-4 p-4 hover:shadow-md' >
                                <h3 className='font-bold' > Projeto: {repo.full_name} </h3>
                                <p> Language: {repo.language} </p>
                                <p> Descrição: {repo.description} </p>
                            </div>
                        )
                    }
                )} 
            </pre>
        </div>
    )
}
export async function getServerSideProps(context){

    const resUser = await fetch('https://api.github.com/users/marcosgray')
    const user = await resUser.json()

    const resRepo = await fetch('https://api.github.com/users/marcosgray/repos?sort=updated')
    const originalRepos = await resRepo.json()

    const dontShowRepos = ['MarcosGray/exemplovue', 'MarcosGray/Sistema_seguros_php']

    const ifJavaScript = repo => repo.language !== 'CSS'
    const dontShowFilter = repo => dontShowRepos.indexOf(repo.full_name) === -1
    const extractData = repo => ({
        id: repo.id,
        full_name: repo.full_name,
        language: repo.language
    })

    const repos = originalRepos.filter(ifJavaScript).filter(dontShowFilter).map(extractData)


    return {
        props: {
            currentDate: new Date().toString(),
            repos,
            user
        }
    }
}
export default Index
